// ChartComponent.js
import React, { useState, useEffect } from 'react';
import Plot from 'react-plotly.js';
import Papa from 'papaparse';

const ChartComponent = () => {
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('../dataset.csv'); // Replace with your actual CSV file path
        const csvData = await response.text();

        const parsedData = Papa.parse(csvData, { header: true });

        const trace1 = {
          x: parsedData.data.map((entry) => entry.x),
          y: parsedData.data.map((entry) => entry.y),
          type: 'scatter',
          mode: 'lines+markers',
          marker: { color: 'red' },
        };

        const trace2 = {
          type: 'bar',
          x: parsedData.data.map((entry) => entry.x),
          y: parsedData.data.map((entry) => entry.y),
        };

        setChartData([trace1, trace2]);
      } catch (error) {
        console.error('Error fetching or parsing CSV data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <Plot
      data={chartData}
      layout={{ width: 800, height: 400, title: 'CSV Data Plot' }}
    />
  );
};

export default ChartComponent;
