import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Chart from "react-apexcharts";

const ChartComponent = () => {


  const [downsampledData, setDownsampledData] = useState([]);
  const[timestamp,setTimeStamp]=useState([]);
  const[percentage,setPercentage]=useState([]);

  useEffect(() => {
    // Fetch downsampled data from the API endpoint
    axios.get('http://localhost:3001/api/downsample')
      .then(response => {
        console.log(response.data.downsampledData);
        setDownsampledData(response.data.downsampledData);
        setTimeStamp(downsampledData.timestamp);
        setPercentage(downsampledData.percentage);
      })
      .catch(error => {
        console.error('Error fetching downsampled data:', error);
      });
  }, []);

  const [state,setState]=useState(
    {
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories:  timestamp
        }
      },
      series: [
        {
          name: "series-1",
          data: percentage
        }
      ]
    });

  return (
    <div>
      <Chart
              options={state.options}
              series={state.series}
              type="area"
              width="500"
            />
    </div>
  );
};

export default ChartComponent;
